INSERT INTO facts(fact, category_id) VALUES ('The Roman Empire was one of the largest empires in history, stretching across Europe, North Africa, and the Middle East.', (SELECT id FROM category WHERE name = 'history')),('The Great Wall of China was built to protect Chinese states and empires against invasions from nomadic groups of the Eurasian Steppe.', (SELECT id FROM category WHERE name = 'history')),('The French Revolution, which began in 1789, drastically changed the political landscape of France and inspired other revolutionary movements.', (SELECT id FROM category WHERE name = 'history')),('The American Civil War, fought from 1861 to 1865, was primarily over the enslavement of Black people in the southern states.', (SELECT id FROM category WHERE name = 'history')),('The Industrial Revolution, beginning in the late 18th century, marked a period of major industrialization and technological innovation in Europe and the United States.', (SELECT id FROM category WHERE name = 'history')),('World War I, also known as the Great War, lasted from 1914 to 1918 and involved many of the world''s great powers.', (SELECT id FROM category WHERE name = 'history')),('World War II, from 1939 to 1945, was the deadliest conflict in human history, involving most of the world''s nations.', (SELECT id FROM category WHERE name = 'history')),('The fall of the Berlin Wall in 1989 marked the symbolic end of the Cold War and the division between East and West Germany.', (SELECT id FROM category WHERE name = 'history')),('The ancient Egyptian civilization, known for its pyramids and pharaohs, flourished along the Nile River for over 3,000 years.', (SELECT id FROM category WHERE name = 'history')),('The Renaissance, spanning from the 14th to the 17th century, was a cultural movement that profoundly influenced European intellectual life, art, and literature.', (SELECT id FROM category WHERE name = 'history'));
INSERT INTO facts(fact, category_id) VALUES ('Portugal was a leading nation during the Age of Discovery. Portuguese explorers, like Vasco da Gama, pioneered sea routes to India, Africa, and South America, establishing a global maritime empire.',(SELECT id FROM category WHERE name = 'history'));
